mapper.update:
 		update orderBook set delNy = 1 
			where 1=1
			and obSeq = (
			  select a.obSeq from 
			    (select o.obSeq,o.timestamp from orderBook o 
			    where 1=1
			    and submittedCrypto = #{crpytoSeq}
			    and orderType = #{orderType}
			    order by timestamp desc limit 1) a
			);
