mapper.select:
select orderStatus from OrderBook where 1=1
and submittedCrypto = #{cryptoSeq}
and obSeq = #{obSeq}

mapper.update:
 		update orderBook set delNy = 0
			where 1=1
			and obSeq = #{obSeq}

 		update orderBook set obAmount = #{obAmount} 
			where 1=1
			and orderStatus = 0
		 	and obSeq = #{obSeq}
