mapper.select:
select count(*) cnt from (
select cryptoSeq ,cryptoName ,cryptoSym ,memberSeq ,ifNull(balance,0) as balance -- 보유 수량 ,Round((((buySum - ifNull(soldSum,0)) / balance)),8) as app -- 매수 평균가 ,(buySum - ifNull(soldSum,0)) as pp -- 매수금액 ,(recentPrice * balance) as valuation -- 평가 금액 ,(recentPrice * balance) - (buySum - ifNull(soldSum,0)) as pnl -- 평가 손익 ,ifNull(Round((((recentPrice * balance) - (buySum - ifNull(soldSum,0)))/(buySum - ifNull(soldSum,0))),2)*100,0) as roi -- 수익률 from ( select e.memberSeq ,c.cryptoSeq as cryptoSeq ,c.cryptoSym as cryptoSym ,c.cryptoName as cryptoName ,ifNull((select balance from exchangeBalance where memberSeq = e.memberSeq and cryptoSeq = 1),0) as cashBalance ,ifNull(balance,0) as cryptoBalance ,(select price from transaction where cryptoSeq = c.cryptoSeq order by timestamp desc limit 1) as recentPrice ,(select sum(price * amount) from transaction where memberSeqBuy = e.memberSeq and cryptoSeq = c.cryptoSeq) as buySum ,(select sum(price * amount) from transaction where memberSeqSell = e.memberSeq and cryptoSeq = c.cryptoSeq) as soldSum ,(select balance from exchangeBalance where memberSeq = e.memberSeq and cryptoSeq = c.cryptoSeq) as balance from exchangeBalance e left join crypto c on e.cryptoSeq = c.cryptoSeq where c.cryptoSeq not in('1') )ts where memberSeq = #{memberSeq}
) cnt

select ifNull(date_format(t.timestamp,'%Y.%m.%d'),'0000-00-00') as timestampMD ,ifNull(date_format(t.timestamp,'%H:%i:%s'),'0000-00-00') as timestampR -- 체결시간 ,c.cryptoName ,c.cryptoSym ,obSeq ,case when bos = 0 then '매수' when bos = 1 then '매도' end as bos ,ifNull(t.price,0) as price ,Round(obAmount,6) as obAmount ,Round((ifNull(t.price,0) * obAmount)) as pp ,ifNull(date_format(o.timestamp,'%Y.%m.%d'),'0000-00-00') as orderTimestampMD ,ifNull(date_format(o.timestamp,'%H:%i:%s'),'0000-00-00') as orderTimestampR from orderBook o left join transaction t on o.obSeq = if(bos = 0, t.obSeqBuy,t.obSeqSell) left join crypto c on o.submittedCrypto = c.cryptoSeq where o.submittedBy = #{memberSeq}
and date_add(now(),interval - ${shDuration}) <= o.timestamp
and bos = #{shOption}
and c.cryptoName = #{shValue}
order by o.timestamp desc limit #{rowNumToShow} offset #{startRnumForMysql}

select count(*) cnt from (
select ifNull(date_format(t.timestamp,'%Y.%m.%d'),'0000-00-00') as timestampMD ,ifNull(date_format(t.timestamp,'%H:%i:%s'),'0000-00-00') as timestampR -- 체결시간 ,c.cryptoName ,c.cryptoSym ,obSeq ,case when bos = 0 then '매수' when bos = 1 then '매도' end as bos ,ifNull(t.price,0) as price ,Round(obAmount,6) ,Round((ifNull(t.price,0) * obAmount)) as pp ,ifNull(date_format(o.timestamp,'%Y.%m.%d'),'0000-00-00') as orderTimestampMD ,ifNull(date_format(o.timestamp,'%H:%i:%s'),'0000-00-00') as orderTimestampR from orderBook o left join transaction t on o.obSeq = if(bos = 0, t.obSeqBuy,t.obSeqSell) left join crypto c on o.submittedCrypto = c.cryptoSeq where o.submittedBy = #{memberSeq}
and date_add(now(),interval - ${shDuration}) <= o.timestamp
and bos = #{shOption}
and c.cryptoName = #{shValue}
) cnt

select count(*) cnt from (
select date_format(ob.timestamp,'%Y.%m.%d') as timestampMD -- 체결시간 ,date_format(ob.timestamp,'%H:%i:%s') as timestampR -- 체결시간 ,obSeq ,c.cryptoSeq -- cryptoSeq ,c.cryptoName -- 코인 이름 ,c.cryptoSym -- 코인 심볼 ,case when bos = 0 then '매수' when bos = 1 then '매도' end as bos-- 거래종류 ,price -- 주문가격 ,ifNull(orderAmount,0) as orderAmount -- 주문수량 ,obAmount -- 미체결량 from orderBook ob left join crypto c on ob.submittedCrypto = c.cryptoSeq left join member m on ob.submittedBy = m.memberSeq where 1=1 and submittedBy = #{memberSeq} and orderStatus = 0 and cryptoSeq not in ('1') order by timestamp desc
) cnt
